      //   backgroundSize: "cover",
      backgroundColor: "#353535",
      color: "#ffffff",
      transition: "0.3s",
    },
  };

  const headerStyles = {
light: {
  background: "#ffffff",
      color: "#333333",
      transition: "0.3s",
},

dark: {
  backgroundColor: "#353540",
      color: "#ffffff",
}
  }

  return (
    <div className="MainWindow" style={{ ...GlobalStyles[theme] }}>
      <Header style={{ ...headerStyles[theme] }}/>
      <ChartWrapper />
      <Inputs />
    </div>
  );
};

export default MainWindow;
