import React from "react";
import "./App.css";
import Inputs from "./components/inputs/Inputs";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import { library } from "@fortawesome/fontawesome-svg-core";
import ChartWrapper from "./components/chartWrapper/ChartWrapper";
import Header from "./components/header/Header";
import { ThemeProvider } from "styled-components";
import { GlobalStyles } from "./components/globalstyle/Globalstyle";
// import { useDarkMode } from "./components/useDarkMode/UseDarkMode";
import { lightTheme, darkTheme } from "./components/themes/Themes";

import { useTheme } from "./components/themeContext/ThemeContext"; // Import the useTheme hook

library.add();

function App() {
  const { theme, toggleTheme } = useTheme(); 

  const lightTheme = {
    background: 'light-background',
  };

  const darkTheme = {
    background: 'dark-background',

  };

  const themeMode = theme === "light" ? lightTheme : darkTheme;

  return (
    <ThemeProvider  theme={themeMode} >
      <>
        <GlobalStyles />
        <div className={`App ${themeMode.ba}`}>
          <Header />
          <ChartWrapper />
          <Inputs />
        </div>
      </>
    </ThemeProvider>
  );
}

export default App;
