import React from "react";
import { useTheme } from "../themeProvider/ThemeProvider"; // Import useTheme hook

function Header() {
  const { theme, toggleTheme } = useTheme(); // Destructure the toggleTheme function

  return (
    <header>
      <h1>Expense Tracker</h1>
      <button onClick={toggleTheme}>Toggle Theme</button> {/* Add the toggle button */}
    </header>
  );
}

export default Header;
