import { ThemeContext } from "../themeProvider/ThemeContext";
import Header from ".";
import Inputs from "./component/input/Inputs";
import ChartWrapper from "./component/chartWrapper/ChartWrapper";
import { useContext } from "react";

const MainWindow = () => {
    const { theme } = useContext(ThemeContext);

    const GlobalStyles = {
    light: {
      backgroundColor: '#b5aa9d',
      color: '#333333',
      transition: '0.3s',
    },
    dark: {
      backgroundColor: '#353535',
      color: '#ffffff',
      transition: '0.3s',
    },
  };

  return (
    <div className="MainWindow" style={{ ...GlobalStyles[theme] }}>
        <Header />
        <ChartWrapper />
        <Inputs />
    </div>
  );
};

export default MainWindow
